#ifndef MOUSEDATA_H
#define MOUSEDATA_H

#include <pandabase.h>

#include "modifierButtons.h"

////////////////////////////////////////////////////////////////////
//       Class : MouseData
// Description : Holds the data that might be generated by a 2-d
//               pointer input device, such as the mouse in the
//               GraphicsWindow.
////////////////////////////////////////////////////////////////////
class EXPCL_PANDA MouseData {
public:
  MouseData();

  int get_x() const;
  int get_y() const;
  bool get_in_window() const;

  bool _in_window;
  int _xpos;
  int _ypos;
};

#endif


